import React, { useState } from "react";
import "./../styles/Login.css";

const Login = () => {
  const [isSignUp, setIsSignUp] = useState(false); // Toggle between login and sign-up
  const [email, setEmail] = useState(""); // State for email input
  const [password, setPassword] = useState(""); // State for password input
  const [name, setName] = useState(""); // State for name input (sign-up form)

  const toggleSignUp = () => {
    setIsSignUp(!isSignUp);
    setEmail("");
    setPassword("");
    setName("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      // Handle sign-up logic
      console.log("Creating account:", { name, email, password });
      alert("Account created successfully!");
    } else {
      // Handle login logic
      console.log("Logging in with:", { email, password });
      if (email && password) {
        alert("Login successful!");
        window.location.href = "/home"; // Redirect to home page
      } else {
        alert("Please fill in both email and password.");
      }
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        {/* Left Section */}
        <div className="login-welcome">
          <h1>Welcome to Ask.AI</h1>
          <p>Your gateway to character generation and imagination.</p>
        </div>

        {/* Right Section */}
        <div className="login-box">
          <h2>{isSignUp ? "Create Account" : "Log In"}</h2>
          <form onSubmit={handleSubmit}>
            {/* Conditional Rendering: Show Name Field for Sign-Up */}
            {isSignUp && (
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <input
                  type="text"
                  id="name"
                  placeholder="Enter your name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
            )}

            {/* Email Field */}
            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            {/* Password Field */}
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            {/* Button */}
            <button type="submit" className="btn-login">
              {isSignUp ? "Create Account" : "Log In"}
            </button>
          </form>

          {/* Toggle between Login and Sign-Up */}
          <div className="toggle-signup">
            <span>Don't have an account? </span>
            <span className="signup-link" onClick={toggleSignUp}>
              {isSignUp ? "Log In" : "Sign Up"}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
